// FILTER

$(document).on("change", "form#filter input", function(){

    $("#filtering").removeClass("d-none");

    var reference_id = $(this).data("reference");

    if($(this).is(":checked"))
    {
        $("input#"+reference_id+"_reference1").prop("checked",true).attr("checked",true);
        $("input#"+reference_id+"_reference2").prop("checked",true).attr("checked",true);
    }
    else
    {
        $("input#"+reference_id+"_reference1").prop("checked",false).attr("checked",false);
        $("input#"+reference_id+"_reference2").prop("checked",false).attr("checked",false);
    }

    //

    run_filter()

})

$(document).on("change", "form#filter select", function(){

    $("#filtering").removeClass("d-none");

    //

    run_filter()

})

$(document).on("click", "a.breadcrumb", function(){

    $("#filtering").removeClass("d-none");

    var reference_id = $(this).data("reference");

    $("input#"+reference_id+"_reference1").prop("checked",false).attr("checked",false);
    $("input#"+reference_id+"_reference2").prop("checked",false).attr("checked",false);

    if($(this).parents("li").find("a").length==1)
    {
        $(this).parents("li").remove();
    }

    $(this).remove();

    //

    run_filter()

})

// RESET FILTER

$(document).on("click", ".reset", function(e){

    e.preventDefault();


    $(".filtering-overlay").removeClass("d-none");

    $("table").addClass("blur")

    //

    document.location.href = 'https://www.spotrac.com/nfl/rankings/player'

});

// SORT

$(document).on("click", "th.sort", function(){

    $("form#filter input[name='sort']").val( $(this).data('sort') );

    //

    run_filter()

});

function run_filter()
{
    //$("#filtering-icon").removeClass("d-none");    
    $(".filtering-overlay").removeClass("d-none");

    $(".alert-filtering").removeClass("d-none");

    //

    var nextURL = $("form#filter").data('action');

    var selections = $("form#filter").serializeArray();

    var params = [];
    var year_exists = false;

    for(var i=0; i<selections.length; i++)
    {
        if(selections[i].name=='year')
        {
            year_exists = selections[i].value;    
        }
    }

    if(!year_exists)
    {
        params['year'] = [];
        params['year'].push( 2025 );
    }

    for(var i=0; i<selections.length; i++)
    {
        if(selections[i].name=='sort')
        {
            params['sort'] = [];
            params['sort'].push( selections[i].value );
        }
        else if(selections[i].name!='sort' && selections[i].value!='')
        {         
            if(!params[ selections[i].name ]) 
            {
                params[selections[i].name] = [];
            }

            params[selections[i].name].push( selections[i].value );
        }
    }



    var params_str = '';
    for (var key in params) 
    {
        var value = params[key];

        params_str += '/'+key+'/'+value.join("+");
    }

     nextURL = nextURL+'/_'+params_str;


    //$("ul.list-group").addClass("blur")
    //$("table").addClass("blur")

    $.ajax({
        type: 'post',
        url: nextURL,
        data: 'ajax=table',
        success: function(data){
            $(".alert-filtering").addClass("d-none");

            $("#table-wrapper").html(data)
        }
    }); 


	// update url history
    window.history.pushState({href: nextURL}, '', nextURL);
}

